mapper.update:
        UPDATE datastore
        SET datstore_name = #{datstoreName}
	        ,datstore_type = #{datstoreType}
	        ,datstore_subtype = #{datstoreSubtype}
	        ,host_name = #{hostName}
	        ,port_num = #{portNum}
	        ,cred_user_info = #{credUserInfo}
	        ,cred_pass_info = #{credPassInfo}
	        ,description = #{description}
	        ,update_date = NOW()
	        ,update_user_id = #{updateUserId}
        WHERE datstore_id = #{datstoreId}

mapper.select:
SELECT datstore_id, datstore_name, datstore_type, datstore_subtype, host_name, port_num, cred_user_info, cred_pass_info, description, update_date, update_user_id
FROM datastore
WHERE datstore_id = #{datstoreId}
